            $lp_lost=25;            $cp_lost=5;        break;    }    $out_put='`3Du gehst hinüber zu dem '.$baum.', plückst dir '.$art2.' '.$frucht.' und beißt hinein.';    $sql = 'SELECT * FROM items WHERE owner='.$session[user][acctid].' AND class=\'Münze\' AND name=\'Zugangsmünze\'';    $result = db_query($sql);    if (db_num_rows($result)>0){        switch(e_rand(1,10)){            case 1:            case 2:            case 3:            case 4:                $out_put.='`3Hmmm, das ist wirklich '.$art.' beste '.$frucht.', '.$art.' du je gegessen hast!';            break;            case 5:                $out_put.='`3Hmmm, das ist wirklich '.$art.' beste '.$frucht.', '.$art.' du je gegessen hast und scheint eine besondere Wirkung zu haben, denn du fühlst, wie dich Energie durchströmt';                $session['user']['hitpoints']*=$hp_gain;            break;        }        addnav('Z?Zurück','klostergarten.php?op=baum');    }else{        $out_put='`3Hmmm, das ist wirklich '.$art.' beste '.$frucht.', '.$art.' du je gegessen hast!';        $out_put.=' Du vergisst das Warnschild, pflückst noch mehr und isst sie.';        $out_put.=' Als du satt bist, setzt du dich unter den Baum und hältst dir den wohlgenährten Bauch, als plötzlich, aus heiterem Himmel, ein `^Bl`6i`^tz`3 auf dich herab fährt!';        $name=$session['user']['name'];        addnews($name.' `^nahm vom Eigentum der Götter und wurde bestraft!');        if ($session['user']['maxhitpoints']>10*$session['user']['level']+$lp_lost && $session['user']['rp_only']==0) {            $session['user']['maxhitpoints']-=$lp_lost;            $out_put.='`n`n`&Du verlierst '.$lp_lost.' Permanente Lebenspunkte!';        } else {            if ($session['user']['charm']>1+$cp_lost){                $session['user']['charm']-=$cp_lost;                $out_put.='`n`n`&Du verlierst '.$cp_lost.' Charmepunkte!';            }        }        $session['user']['hitpoints']=1;        addnav('Z?Zurück','klostergarten.php?op=baum');    }} elseif($_GET['op']==""){    $out_put='`b`c`2Der Klostergarten`c`b`n';    $out_put.='`2Du betrittst den Klostergarten, einen Ort der Ruhe, in dem die Mönche ihre Entspannung von den Mühen des Tages suchen und wo auch die Obstbäume des Klosters stehen.';    $out_put.=' Die Luft ist angenehm warm, die Wiesen sind saftig grün und überall blühen Blumen in den schönsten Farben, in der Mitte des Parkes ist ein kleiner Teich, in dessen Mitte leise ein Springbrunnen plätschert.';    $out_put.=' Der Garten ist umgeben von Baumreihen, durch die sacht der Wind streicht und erzählt von seinen Reisen in die Ferne.';    $out_put.=' Die Vögel singen ein fröhliches Lied und Schmetterlinge ziehen ihre Bahnen von Blume zu Blume.`n';    $out_put.='Direkt am Eingang steht ein Schild, auf dem darauf hin gewiesen wird, dass hier ein Ort des Friedens, der Ruhe und Entspannung ist.';    $out_put.=' Auf einer Bank am Eingang sitzen Gäste und unterhalten sich:`n`n';    $vc=true;        addnav('O?Zu den Obstbäumen','klostergarten.php?op=baum');    addnav('Z?Zurück zum Klosterhof','kloster.php');}output($out_put);if($vc) viewcommentary('klostergarten','Hinzufügen',25,'flüstert',1,1);page_footer();

